	local.R.NodeScan = nil
	if err = local.L.LoadNodeScan(ctx, tx, true, &local, nil); err != nil {
		t.Fatal(err)
	}
	if local.R.NodeScan == nil {
		t.Error("struct should have been eager loaded")
	}
